package com.syntax.class03;

public class CompoundOperations {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int num=100;
		num=num+10;
		System.out.println(num);
		
		num=num+200;
		System.out.println(num);
		
		int myNumber = 100;
		myNumber+=10;
		System.out.println(myNumber);
		
		myNumber+=200;
		System.out.println(myNumber);
		
		myNumber -=100;
		System.out.println(myNumber);
		
		myNumber /=10;
		System.out.println(myNumber);
		
		myNumber *=1000;
		System.out.println(myNumber);
		
		myNumber %=100;
		System.out.println(myNumber);
		
		int var1 = 10;
		
		var1 +=100;
		System.out.println(var1); //110
		var1 -=67;
		System.out.println(var1); //43
		int cakePiece= 11;
		
		cakePiece /=4;
		System.out.println(cakePiece); //2
		
		int cake = 25;
		
		cake %=7;
		System.out.println(cake); //4
		
		
		
				
	}

}
